CREATE TABLE `afdelingen` (`id` int(11) NOT NULL AUTO_INCREMENT, `naam` varchar(32) NOT NULL, PRIMARY KEY (`id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8 AUTO_INCREMENT=1;
CREATE TABLE `audit_trail` (`id` int(11) NOT NULL AUTO_INCREMENT, `persoon` int(11) NOT NULL, `dt` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP, `query` varchar(255) NOT NULL, PRIMARY KEY (`id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8 AUTO_INCREMENT=1;
CREATE TABLE `geopackages` (`id` int(11) NOT NULL AUTO_INCREMENT, `naam` varchar(64) NOT NULL, `kaartnaam` varchar(64) NOT NULL, `afdeling` int(11) NOT NULL, `onderwerp` int(11) NOT NULL, `soort` char(6) NOT NULL,  `brongeopackage` varchar(255) NOT NULL, `indatalink` varchar(255) NOT NULL, `datalink` varchar(255) NOT NULL, `opmaak` varchar(255) NOT NULL, `wms` char(1) NOT NULL, `wfs` char(1) NOT NULL, `wcs` char(1) NOT NULL, `wmts` char(1) NOT NULL, PRIMARY KEY (`id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8 AUTO_INCREMENT=1;
CREATE TABLE `onderwerpen` (`id` int(11) NOT NULL AUTO_INCREMENT, `naam` varchar(64) NOT NULL, `afkorting` varchar(32) NOT NULL, `afdeling` int(11) NOT NULL, PRIMARY KEY (`id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8 AUTO_INCREMENT=1;
CREATE TABLE `personen` (`id` int(11) NOT NULL AUTO_INCREMENT, `naam` varchar(32) NOT NULL, `afdeling` int(11) NOT NULL, `email` varchar(32) NOT NULL, `ad_account` varchar(32) NOT NULL, `password` varchar(255) NOT NULL, `admin` char(1) NOT NULL, `afd_admin` char(1) NOT NULL, PRIMARY KEY (`id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8 AUTO_INCREMENT=1;

